import axios from 'axios';
import React, { useState, useEffect } from 'react';
import useAuth from '../hooks/useAuth';
import { useNavigate, Link, useParams } from 'react-router-dom';
import { Navigate } from "react-router-dom";

function Project() {

  const navigate = useNavigate();
  const { token, setToken } = useAuth();
  const [user, setUser] = useState();

  const {id} = useParams();

  const [project, setProject] = useState(null);


  async function getProject() {
    try {
      const response = await axios.get('http://localhost:4000/project/'+id);
      console.log(response);

      setProject(response.data)
    } catch (error) {
      console.error(error);
    }
  } 


  const showAuth = () => {
    console.log(token)
  }

  const handleLogout = () => {
    // Lösche den Token aus dem Local Storage
    localStorage.removeItem('token');
    localStorage.removeItem('user')
    localStorage.removeItem('expiration')
    // Setze den Token auf null im AuthContext
    setToken(null);
    // Setze den Benutzer auf null
    setUser(null);
    // Optional: Weiterleitung zur Login-Seite
    navigate('/login');
  };


  useEffect(() => {
    getProject();

    const user = JSON.parse(localStorage.getItem('user'));
    if (user?.username) {
      setUser(user);

      console.log("user: " + user)
    } else {

      /*   console.log("user existiert nicht")
        handleLogout() */
    }


  }, [user])

  if (!token || Object.keys(token).length < 1)

    return (
      <Navigate to="/login" />
    )


  return (
    <div className="main" style={{ width: '100%' }}>


      <div className="projects" style={{ width: '50%' }}>
       <h1>{project?.name}</h1>
       <p>{project?.description}</p>
      </div>

      <button type="button" onClick={showAuth}>Show Auth</button>

      <button type="button" onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default Project;
