import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useAuth from '../hooks/useAuth';
import { useNavigate, useParams } from 'react-router-dom';
import { Navigate } from "react-router-dom";
import TaskCreationDialog from './TaskCreationDialog'; // Importiere den Dialog

function Project() {
  const navigate = useNavigate();
  const { token, setToken } = useAuth();
  const { id } = useParams();
  const [user, setUser] = useState();
  const [project, setProject] = useState(null);
  const [projectUsers, setProjectUsers] = useState([]);
  const [projectTasks, setProjectTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [isTaskDialogOpen, setTaskDialogOpen] = useState(false); // Zustand für die Anzeige des Dialogs

  async function getProject() {
    try {
      const response = await axios.get(`http://localhost:4000/project/${id}`);
      setProject(response.data);
    } catch (error) {
      console.error(error);
    }
  } 

  // Andere Funktionen hier...

  const handleOpenTaskDialog = () => {
    setTaskDialogOpen(true); // Öffne den Dialog
  };

  const handleCloseTaskDialog = () => {
    setTaskDialogOpen(false); // Schließe den Dialog
  };

  useEffect(() => {
    Promise.all([getProject(), getProjectUsers(), getProjectTasks(), getUsers(), getTasks()])
      .then(() => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user?.username) {
          setUser(user);
        }
      })
      .catch(error => console.error(error));
  }, []);

  if (!token || Object.keys(token).length < 1)
    return <Navigate to="/login" />

  return (
    <div className="main" style={{ width: '100%' }}>
      <div className="projects" style={{ width: '50%' }}>
        <h1>{project?.name}</h1>
        <p>{project?.description}</p>
        <h2>Project Participants</h2>
        <ul>
          {projectUsers
            .filter(user => user.project_id === Number(id))
            .map(user => (
              <li key={user.user_id}>
                {users.find(u => u.id === user.user_id)?.username || 'Unknown User'}
              </li>
            ))}
        </ul>
        <h2>Project Tasks</h2>
        <ul>
          {projectTasks
            .filter(task => task.project_id === Number(id))
            .map(task => (
              <li key={task.task_id}>
                {tasks.find(t => t.id === task.task_id)?.name || 'Unknown Task'}
              </li>
            ))}
        </ul>
        <button type="button" onClick={handleOpenTaskDialog}>Create Task</button> {/* Button zum Öffnen des Dialogs */}
      </div>
      <button type="button" onClick={handleLogout}>Logout</button>

      {/* Rendern des Dialogs */}
      <TaskCreationDialog open={isTaskDialogOpen} handleClose={handleCloseTaskDialog} />
    </div>
  );
}

export default Project;
