import { Navigate, Outlet } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { useEffect, useState } from "react";

const RequireAuth = () => {
    const { token, setToken } = useAuth();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Überprüfen, ob der Benutzer angemeldet ist, indem der Token im Authentifizierungshook abgerufen wird
        setIsLoading(false);
    }, [token]);

    if (isLoading) {
        return (
            <div className="spinner-border text-primary" role="status">
                <span className="sr-only"></span>
            </div>
        );
    }

    // Wenn der Benutzer angemeldet ist, wird der Inhalt gerendert, andernfalls wird zur Anmeldeseite navigiert
    return token ? <Outlet /> : <Navigate to="/login" />;
}

export default RequireAuth;
