import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../routes/AppRoutes'; // Annahme: Der Pfad zur AuthContext-Datei

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { login } = useAuth(); // Zugriff auf die Login-Funktion aus dem AuthContext

    const axiosInstance = axios.create({ timeout: 2000, baseURL: "http://localhost:4000", withCredentials: false })

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const token = btoa(`${username}:${password}`);
            const response = await axiosInstance.post(
                '/login',
                {},
                {
                    headers: {
                        Authorization: `Basic ${token}`
                    }
                }
            );
            console.log(response.data); // Erfolgreiche Antwort vom Server
            login(token); // Token speichern, wenn erfolgreich eingeloggt
            // Hier könntest du die Weiterleitung zur nächsten Seite oder eine Benachrichtigung einrichten
        } catch (err) {
            console.error('Fehler beim Login:', err);
            setError('Fehler beim Login');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {error && <div>{error}</div>}
            <form onSubmit={handleLogin}>
                <div>
                    <label>Username:</label>
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
