import axios from 'axios';
import React, { useState, useEffect } from 'react';
import useAuth from '../hooks/useAuth';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Main() {

  const { token, setToken } = useAuth();
  const [tasks, settasks] = useState(null);

 /*  const { user, setUser } = useState();  */

  async function gettasks() {
    try {
      const response = await axios.get('http://localhost:4000/tasks');
      console.log(response);

      settasks(response.data)
    } catch (error) {
      console.error(error);
    }
  }


  const showAuth = () => {
    console.log(token)
  }

  const handleLogout = () => {
    // Lösche den Token aus dem Local Storage
    localStorage.removeItem('token');
    // Setze den Token auf null im AuthContext
    setToken(null);
    // Setze den Benutzer auf null
 /*    setUser(null); */
    // Optional: Weiterleitung zur Login-Seite
    // history.push('/login');
  };


  useEffect(() => {
    gettasks();

    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
        setUser(user);
    }


  }, [])

  return (
    <div className="main">
      <h1>Task-App!</h1>

      <h2>{user?.name && "welcome "+ user.name}</h2>

      <ul className="task-container" style={{ width: ' 50%' }}>
        {tasks && tasks.length > 0 && tasks.map((task) => {
          return (
            <>
              <h3 key={task.id}>{task.name}</h3>
              <li key={task.id}>{task.description}</li>
            </>


          )
        })}
      </ul>

      <button type="button" onClick={showAuth}>Show Auth</button>

      <button type="button" onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default Main;
